import React, { Component } from 'react';
import axios from 'axios';
import apiKey from '../config';
import SearchForm from './SearchForm';
import Navigation from './Navigation';
import PhotoContainer from './PhotoContainer';


class Container extends Component {

  constructor() {
    super();
    this.state = {
      photos: [],
      loading: true
    };
  }

  componentDidMount() {
    this.searchForPhotos(this.props.match.params.query);
    this.props.history.listen(location => this.searchForPhotos(location.pathname.slice(8)));
  }

  searchForPhotos = (query = 'bears') => {
    axios.get(`https://www.flickr.com/services/rest/?method=flickr.photos.search&api_key=${apiKey}&text=${query}&format=json`)

    .then(response => {
      this.setState({
        photos: response.data.photos.photo,
        loading: false
      });
    })
    .catch(error => {
      console.log("ERROR", error);
    })
  }

  render() {
    return(
      <div className="container">
        <SearchForm />
        <Navigation />
        {this.state.loading ? (<h2>Loading...</h2>) : (<PhotoContainer data={this.state.photos} />)}
      </div>
    );
  };
}

export default Container;
